import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { RegisterUserService } from 'src/app/service/register-user.service';
// import { UserService } from '../services/user.service';


@Component({
  selector: 'app-user-registration',
  templateUrl: './user-registration.component.html',
  styleUrls: ['./user-registration.component.scss']
})
export class UserRegistrationComponent implements OnInit{
  registrationForm: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private userService: RegisterUserService,
    private router : Router
  ) {
    this.registrationForm = this.formBuilder.group({
      username: ['', [Validators.required]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
    });
  }

  ngOnInit(): void {
}

register() {
  if (this.registrationForm.valid) {
    const userData = this.registrationForm.value;
    this.userService.registerUser(userData).subscribe(
      (response) => {
        // Handle successful registration
        console.log('User registered successfully!', response);
        this.router.navigate(['/login'])
        // You can redirect the user to a login page or perform other actions as needed.
      },
      (error) => {
        // Handle registration errors
        console.error('Registration failed!', error);
      }
    );
  }
}

}


<div id="outer-div">
    <div id="inner-div"> 
        <img/>
    </div>
    <div id="outer-container">
        <div id="container">
            <div id="registeration-container">
                <h1>Register here</h1>
                <form  (ngSubmit)="openPayment()">
                    <div id="form-container">
                        <div class="form-class">
                            <label for="username">Username:</label>
                            <input  placeholder= "Username" type="text" id="username" required>
                            <!-- <div>
                              <span class="error-message">Username is required.</span>
                            </div> -->
                          </div>

                          <div class="form-class">
                            <label for="email">Email:</label>
                            <input placeholder= "Email" type="email" id="email" required>
                            <!-- <div>
                              <span class="error-message">Valid email is required.</span>
                            </div> -->
                          </div>

                          <div class="form-class">
                            <label for="password">Password:</label>
                            <input placeholder= "Password" type="password" id="password" required>
                            <!-- <div>
                              <span class="error-message">Password is required and should have at least 6 characters.</span>
                            </div> -->
                          </div>

                    </div>
                    <button (click)="register()" type="submit">Register</button>
                  </form>
            </div>

        </div>
    </div>

</div>
